import React, { useRef, useState, useCallback } from 'react';
import {
  Box,
  Button,
  VStack,
  HStack,
  Text,
  useToast,
  Image,
  Modal,
  ModalOverlay,
  ModalContent,
  ModalHeader,
  ModalBody,
  ModalFooter,
  useDisclosure,
} from '@chakra-ui/react';
import Webcam from 'react-webcam';
import { useWeb3React } from '@web3-react/core';
import { MdCamera, MdRefresh } from 'react-icons/md';
import axios from 'axios';

function CameraView() {
  const webcamRef = useRef(null);
  const [imgSrc, setImgSrc] = useState(null);
  const { isOpen, onOpen, onClose } = useDisclosure();
  const { account, active } = useWeb3React();
  const toast = useToast();

  const capture = useCallback(() => {
    const imageSrc = webcamRef.current.getScreenshot();
    setImgSrc(imageSrc);
    onOpen();
  }, [webcamRef, onOpen]);

  const handleSave = async () => {
    if (!active) {
      toast({
        title: 'Wallet not connected',
        description: 'Please connect your MetaMask wallet first.',
        status: 'error',
        duration: 5000,
      });
      return;
    }

    try {
      // Convert base64 to blob
      const response = await fetch(imgSrc);
      const blob = await response.blob();
      const file = new File([blob], 'capture.jpg', { type: 'image/jpeg' });

      // Create form data
      const formData = new FormData();
      formData.append('image', file);
      formData.append('wallet_address', account);

      // Send to backend
      const result = await axios.post('http://localhost:5000/capture', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      toast({
        title: 'Success!',
        description: `Image captured and minted as NFT. Token ID: ${result.data.data.token_id}`,
        status: 'success',
        duration: 5000,
      });

      onClose();
      setImgSrc(null);
    } catch (error) {
      toast({
        title: 'Error',
        description: error.message,
        status: 'error',
        duration: 5000,
      });
    }
  };

  const videoConstraints = {
    width: 1280,
    height: 720,
    facingMode: "user"
  };

  return (
    <Box>
      <VStack spacing={8} align="center">
        <Box
          w="100%"
          maxW="1280px"
          h="720px"
          bg="gray.800"
          borderRadius="lg"
          overflow="hidden"
          position="relative"
        >
          <Webcam
            audio={false}
            ref={webcamRef}
            screenshotFormat="image/jpeg"
            videoConstraints={videoConstraints}
            style={{
              width: '100%',
              height: '100%',
              objectFit: 'cover',
            }}
          />
          <Box
            position="absolute"
            bottom="4"
            left="50%"
            transform="translateX(-50%)"
          >
            <HStack spacing={4}>
              <Button
                leftIcon={<MdCamera />}
                colorScheme="blue"
                size="lg"
                onClick={capture}
              >
                Capture Photo
              </Button>
              <Button
                leftIcon={<MdRefresh />}
                colorScheme="gray"
                size="lg"
                onClick={() => setImgSrc(null)}
              >
                Reset
              </Button>
            </HStack>
          </Box>
        </Box>

        <Text color="gray.400">
          {active
            ? 'Click capture to take a photo and mint it as an NFT'
            : 'Please connect your wallet to mint NFTs'}
        </Text>
      </VStack>

      <Modal isOpen={isOpen} onClose={onClose} size="xl">
        <ModalOverlay />
        <ModalContent bg="gray.800">
          <ModalHeader color="white">Preview Capture</ModalHeader>
          <ModalBody>
            <Image src={imgSrc} alt="Captured" borderRadius="md" />
          </ModalBody>
          <ModalFooter>
            <Button colorScheme="blue" mr={3} onClick={handleSave}>
              Save & Mint NFT
            </Button>
            <Button variant="ghost" onClick={onClose} color="white">
              Cancel
            </Button>
          </ModalFooter>
        </ModalContent>
      </Modal>
    </Box>
  );
}

export default CameraView;
